        /// <remarks>Pass a parameter value of null to not filter by this in a fluid call chain.</remarks>
        /// <param name="query">The query to act on.</param>
        /// <param name="model">The search model to filter by.</param>
        /// <returns>The <see cref="IQueryable{Contractor}" /> with an additional Where applied if the parameter has a value.</returns>
        public static IQueryable<Contractor> FilterContractorsBySearchModel(
                this IQueryable<Contractor> query,
                IContractorSearchModel model)
        {
            if (model == null)
            {
                return query;
            }
            var query2 = Contract.RequiresNotNull(query)
                .FilterByBaseSearchModel(model)
                .FilterContractorsByAccountID(model.AccountID, model.AccountIDIncludeNull)
                .FilterContractorsByStoreID(model.StoreID, model.StoreIDIncludeNull)
                .FilterContractorsByUserID(model.UserID, model.UserIDIncludeNull)
                ;
            return query2;
        }

