        public static IQueryable<TEntity> FilterDiscountCodesByCode<TEntity>(
                this IQueryable<TEntity> query,
                string? parameter,
                bool? strict,
                bool? includeNull)
            where TEntity : class, IDiscountCode
        {
            if (includeNull != true && !Contract.CheckValidKey(parameter))
            {
                return query;
