        public static IQueryable<TEntity> FilterPurchaseOrderItemsByStatus<TEntity>(
                this IQueryable<TEntity> query,
                string? parameter,
                bool? strict,
                bool? includeNull)
            where TEntity : class, IPurchaseOrderItem
        {
            if (includeNull != true && !Contract.CheckValidKey(parameter))
            {
                return query;
