        public static void GenAdAccountSQLSelectorFull()
        {
            PreBuiltAdAccountSQLSelectorFull = x => x == null ? null! : new AnonAdAccount
            {
                MasterID = x.MasterID,
                Master = ModelMapperForAd.PreBuiltAdSQLSelectorList.Expand().Compile().Invoke(x.Master!),
                SlaveID = x.SlaveID,
                Slave = ModelMapperForAccount.PreBuiltAccountSQLSelectorList.Expand().Compile().Invoke(x.Slave!),
                ID = x.ID,
                CustomKey = x.CustomKey,
                CreatedDate = x.CreatedDate,
                UpdatedDate = x.UpdatedDate,
                Active = x.Active,
                Hash = x.Hash,
                JsonAttributes = x.JsonAttributes,
            };
        }

