        public static void GenAdBrandSQLSelectorFull()
        {
            PreBuiltAdBrandSQLSelectorFull = x => x == null ? null! : new AnonAdBrand
            {
                MasterID = x.MasterID,
                Master = ModelMapperForAd.PreBuiltAdSQLSelectorList.Expand().Compile().Invoke(x.Master!),
                SlaveID = x.SlaveID,
                Slave = ModelMapperForBrand.PreBuiltBrandSQLSelectorList.Expand().Compile().Invoke(x.Slave!),
                ID = x.ID,
                CustomKey = x.CustomKey,
                CreatedDate = x.CreatedDate,
                UpdatedDate = x.UpdatedDate,
                Active = x.Active,
                Hash = x.Hash,
                JsonAttributes = x.JsonAttributes,
            };
        }

