        public static IAdStoreModel? CreateAdStoreModelFromEntity(
            this IAdStore? entity,
            MappingMode mode,
            string? contextProfileName)
        {
            if (entity == null) { return null; }
            // Map the Inherited Properties
            // ReSharper disable once InvokeAsExtensionMethod
            var model = BaseModelMapper.MapBaseEntityPropertiesToModel(
                RegistryLoaderWrapper.GetInstance<IAdStoreModel>(contextProfileName),
                Contract.RequiresNotNull(entity),
                mode,
                contextProfileName);
            // Map this level's Properties
            switch (mode)
            {
                case MappingMode.Full: //////////////////////////////////////////////////////////////////////////////////////////////////////////////
                {
                    // AdStore's Properties
                    // AdStore's Related Objects
                    model.Slave = ModelMapperForStore.CreateStoreModelFromEntityLite(entity.Slave, contextProfileName);
                    // AdStore's Associated Objects
                    // Additional Mappings
                    if (CreateAdStoreModelFromEntityHooksFull != null) { model = CreateAdStoreModelFromEntityHooksFull(entity, model, contextProfileName); }
                    goto case MappingMode.Lite;
                }
                case MappingMode.Lite: //////////////////////////////////////////////////////////////////////////////////////////////////////////////
                {
                    // AdStore's Properties
                    // AdStore's Related Objects (Not Mapped unless Forced, or a flattening property)
                    // AdStore's Associated Objects (Not Mapped unless Forced)
                    // Additional Mappings
                    if (CreateAdStoreModelFromEntityHooksLite != null) { model = CreateAdStoreModelFromEntityHooksLite(entity, model, contextProfileName); }
                    goto case MappingMode.List;
                }
                case MappingMode.List: //////////////////////////////////////////////////////////////////////////////////////////////////////////////
                default:
                {
                    // IHaveJsonAttributesBase Properties (Forced)
                    model.SerializableAttributes = entity.JsonAttributes.DeserializeAttributesDictionary();
                    // AdStore's Properties
                    // AdStore's Related Objects (Not Mapped unless Forced, or a flattening property)
                    model.MasterID = entity.MasterID;
                    model.MasterKey = entity.Master?.CustomKey;
                    model.MasterName = entity.Master?.Name;
                    model.SlaveID = entity.SlaveID;
                    model.SlaveKey = entity.Slave?.CustomKey;
                    model.SlaveName = entity.Slave?.Name;
                    // AdStore's Associated Objects (Not Mapped unless Forced)
                    // Additional Mappings
                    if (CreateAdStoreModelFromEntityHooksList != null) { model = CreateAdStoreModelFromEntityHooksList(entity, model, contextProfileName); }
                    break;
                }
            } ///////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
            // Finished!
            return model;
        }
