            {
                MasterID = x.MasterID,
                Master = ModelMapperForAd.PreBuiltAdSQLSelectorList.Expand().Compile().Invoke(x.Master!),
                SlaveID = x.SlaveID,
                Slave = ModelMapperForZone.PreBuiltZoneSQLSelectorList.Expand().Compile().Invoke(x.Slave!),
                ImpressionCounterID = x.ImpressionCounterID,
                ImpressionCounter = ModelMapperForCounter.PreBuiltCounterSQLSelectorList.Expand().Compile().Invoke(x.ImpressionCounter!),
                ClickCounterID = x.ClickCounterID,
                ClickCounter = ModelMapperForCounter.PreBuiltCounterSQLSelectorList.Expand().Compile().Invoke(x.ClickCounter!),
                AdZoneAccessID = x.AdZoneAccessID,
                AdZoneAccess = ModelMapperForAdZoneAccess.PreBuiltAdZoneAccessSQLSelectorList.Expand().Compile().Invoke(x.AdZoneAccess!),
                ID = x.ID,
                CustomKey = x.CustomKey,
                CreatedDate = x.CreatedDate,
                UpdatedDate = x.UpdatedDate,
                Active = x.Active,
                Hash = x.Hash,
                JsonAttributes = x.JsonAttributes,
            };
        }

