        public static IAdZoneModel? CreateAdZoneModelFromEntity(
            this IAdZone? entity,
            MappingMode mode,
            string? contextProfileName)
        {
            if (entity == null) { return null; }
            // Map the Inherited Properties
            // ReSharper disable once InvokeAsExtensionMethod
            var model = BaseModelMapper.MapBaseEntityPropertiesToModel(
                RegistryLoaderWrapper.GetInstance<IAdZoneModel>(contextProfileName),
                Contract.RequiresNotNull(entity),
                mode,
                contextProfileName);
            // Map this level's Properties
            switch (mode)
            {
                case MappingMode.Full: //////////////////////////////////////////////////////////////////////////////////////////////////////////////
                {
                    // AdZone's Properties
                    // AdZone's Related Objects
                    model.AdZoneAccess = ModelMapperForAdZoneAccess.CreateAdZoneAccessModelFromEntityLite(entity.AdZoneAccess, contextProfileName);
                    model.ClickCounter = ModelMapperForCounter.CreateCounterModelFromEntityLite(entity.ClickCounter, contextProfileName);
                    model.ImpressionCounter = ModelMapperForCounter.CreateCounterModelFromEntityLite(entity.ImpressionCounter, contextProfileName);
                    model.Slave = ModelMapperForZone.CreateZoneModelFromEntityLite(entity.Slave, contextProfileName);
                    // AdZone's Associated Objects
                    // Additional Mappings
                    if (CreateAdZoneModelFromEntityHooksFull != null) { model = CreateAdZoneModelFromEntityHooksFull(entity, model, contextProfileName); }
                    goto case MappingMode.Lite;
                }
                case MappingMode.Lite: //////////////////////////////////////////////////////////////////////////////////////////////////////////////
                {
                    // AdZone's Properties
                    // AdZone's Related Objects (Not Mapped unless Forced, or a flattening property)
                    // AdZone's Associated Objects (Not Mapped unless Forced)
                    // Additional Mappings
                    if (CreateAdZoneModelFromEntityHooksLite != null) { model = CreateAdZoneModelFromEntityHooksLite(entity, model, contextProfileName); }
                    goto case MappingMode.List;
                }
                case MappingMode.List: //////////////////////////////////////////////////////////////////////////////////////////////////////////////
                default:
                {
                    // IHaveJsonAttributesBase Properties (Forced)
                    model.SerializableAttributes = entity.JsonAttributes.DeserializeAttributesDictionary();
                    // AdZone's Properties
                    // AdZone's Related Objects (Not Mapped unless Forced, or a flattening property)
                    model.AdZoneAccessID = entity.AdZoneAccessID;
                    model.AdZoneAccessKey = entity.AdZoneAccess?.CustomKey;
                    model.AdZoneAccessName = entity.AdZoneAccess?.Name;
                    model.ClickCounterID = entity.ClickCounterID;
                    model.ClickCounterKey = entity.ClickCounter?.CustomKey;
                    model.ImpressionCounterID = entity.ImpressionCounterID;
                    model.ImpressionCounterKey = entity.ImpressionCounter?.CustomKey;
                    model.MasterID = entity.MasterID;
                    model.MasterKey = entity.Master?.CustomKey;
                    model.MasterName = entity.Master?.Name;
                    model.SlaveID = entity.SlaveID;
                    model.SlaveKey = entity.Slave?.CustomKey;
                    model.SlaveName = entity.Slave?.Name;
                    // AdZone's Associated Objects (Not Mapped unless Forced)
                    // Additional Mappings
                    if (CreateAdZoneModelFromEntityHooksList != null) { model = CreateAdZoneModelFromEntityHooksList(entity, model, contextProfileName); }
                    break;
                }
            } ///////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
            // Finished!
            return model;
        }
