        public static void GenAuctionCategorySQLSelectorFull()
        {
            PreBuiltAuctionCategorySQLSelectorFull = x => x == null ? null! : new AnonAuctionCategory
            {
                MasterID = x.MasterID,
                Master = ModelMapperForAuction.PreBuiltAuctionSQLSelectorList.Expand().Compile().Invoke(x.Master!),
                SlaveID = x.SlaveID,
                Slave = ModelMapperForCategory.PreBuiltCategorySQLSelectorList.Expand().Compile().Invoke(x.Slave!),
                SortOrder = x.SortOrder,
                ID = x.ID,
                CustomKey = x.CustomKey,
                CreatedDate = x.CreatedDate,
                UpdatedDate = x.UpdatedDate,
                Active = x.Active,
                Hash = x.Hash,
                JsonAttributes = x.JsonAttributes,
            };
        }

