        /// <summary>An <see cref="IBidModel"/> extension method that creates a(n) <see cref="Bid"/> entity.</summary>
        /// <param name="model">            The model to act on.</param>
        /// <param name="timestamp">        The timestamp Date/Time.</param>
        /// <param name="contextProfileName">Name of the context profile.</param>
        /// <returns>The new <see cref="Bid"/> entity.</returns>
        public static IBid CreateBidEntity(
            this IBidModel model,
            DateTime timestamp,
            string? contextProfileName)
        {
            // Create the object and Map the Inherited Properties
            return Contract.RequiresNotNull(model)
                .CreateEntityFromModelBase<IBidModel, Bid>(timestamp, contextProfileName)
                // Use the Update method to map non-inherited properties
                .UpdateBidFromModel(model, timestamp, null);
        }

        /// <summary>An <see cref="IBidModel"/> extension method that updates a(n) <see cref="Bid"/> entity.</summary>
        /// <param name="entity">         The entity to act on.</param>
        /// <param name="model">          The model to read from.</param>
        /// <param name="timestamp">      The timestamp Date/Time.</param>
        /// <param name="updateTimestamp">The update timestamp Date/Time.</param>
        /// <returns>The updated <see cref="Bid"/> entity.</returns>
        public static IBid UpdateBidFromModel(
            this IBid entity,
            IBidModel model,
            DateTime timestamp,
            DateTime? updateTimestamp)
        {
            // Map the Inherited Properties
            entity = Contract.RequiresNotNull(entity)
                .MapBaseModelPropertiesToEntity(Contract.RequiresNotNull(model));
            // Bid Properties
            entity.CurrentBid = model.CurrentBid;
            entity.MaxBid = model.MaxBid;
            entity.Won = model.Won;
            // Bid's Related Objects
            // Finally, update the timestamp
            entity.UpdatedDate = updateTimestamp == null || updateTimestamp == DateTime.MinValue ? null : updateTimestamp;
            // Return
            return entity;
        }

        public static void GenBidSQLSelectorFull()
        {
            PreBuiltBidSQLSelectorFull = x => x == null ? null! : new AnonBid
            {
                StatusID = x.StatusID,
                Status = ModelMapperForBidStatus.PreBuiltBidStatusSQLSelectorList.Expand().Compile().Invoke(x.Status!),
                UserID = x.UserID,
                User = ModelMapperForUser.PreBuiltUserSQLSelectorList.Expand().Compile().Invoke(x.User!),
                Won = x.Won,
                MaxBid = x.MaxBid,
                CurrentBid = x.CurrentBid,
                LotID = x.LotID,
                Lot = ModelMapperForLot.PreBuiltLotSQLSelectorList.Expand().Compile().Invoke(x.Lot!),
                ID = x.ID,
                CustomKey = x.CustomKey,
                CreatedDate = x.CreatedDate,
                UpdatedDate = x.UpdatedDate,
                Active = x.Active,
                Hash = x.Hash,
                JsonAttributes = x.JsonAttributes,
            };
        }

