            BaseModelMapper.Initialize(PreBuiltBidSQLSelectorFull == null);
            return dbSetWithFilters
                .AsExpandable()
                .Select(PreBuiltBidSQLSelectorFull!.Compile())
                .ToList()
                .Select(x => CreateBidModelFromEntityFull(x, contextProfileName))!;
