        public static IBidModel? CreateBidModelFromEntity(
            this IBid? entity,
            MappingMode mode,
            string? contextProfileName)
        {
            if (entity == null) { return null; }
            // Map the Inherited Properties
            // ReSharper disable once InvokeAsExtensionMethod
            var model = BaseModelMapper.MapBaseEntityPropertiesToModel(
                RegistryLoaderWrapper.GetInstance<IBidModel>(contextProfileName),
                Contract.RequiresNotNull(entity),
                mode,
                contextProfileName);
            // Map this level's Properties
            switch (mode)
            {
                case MappingMode.Full: //////////////////////////////////////////////////////////////////////////////////////////////////////////////
                {
                    // IHaveAStatusBase Properties
                    model.Status = ModelMapperForBidStatus.CreateBidStatusModelFromEntityLite(entity.Status, contextProfileName);
                    // Bid's Properties
                    // Bid's Related Objects
                    model.Lot = ModelMapperForLot.CreateLotModelFromEntityLite(entity.Lot, contextProfileName);
                    model.User = ModelMapperForUser.CreateUserModelFromEntityLite(entity.User, contextProfileName);
                    // Bid's Associated Objects
                    // Additional Mappings
                    if (CreateBidModelFromEntityHooksFull != null) { model = CreateBidModelFromEntityHooksFull(entity, model, contextProfileName); }
                    goto case MappingMode.Lite;
                }
                case MappingMode.Lite: //////////////////////////////////////////////////////////////////////////////////////////////////////////////
                {
                    // Bid's Properties
                    // Bid's Related Objects (Not Mapped unless Forced, or a flattening property)
                    // Bid's Associated Objects (Not Mapped unless Forced)
                    // Additional Mappings
                    if (CreateBidModelFromEntityHooksLite != null) { model = CreateBidModelFromEntityHooksLite(entity, model, contextProfileName); }
                    goto case MappingMode.List;
                }
                case MappingMode.List: //////////////////////////////////////////////////////////////////////////////////////////////////////////////
                default:
                {
                    // IHaveAStatusBase Properties
                    model.StatusID = entity.StatusID;
                    if (entity.Status != null)
                    {
                        model.StatusKey = entity.Status.CustomKey;
                        model.StatusName = entity.Status.Name;
                        model.StatusDisplayName = entity.Status.DisplayName;
                        model.StatusTranslationKey = entity.Status.TranslationKey;
                        model.StatusSortOrder = entity.Status.SortOrder;
                    }
                    // IHaveJsonAttributesBase Properties (Forced)
                    model.SerializableAttributes = entity.JsonAttributes.DeserializeAttributesDictionary();
                    // Bid's Properties
                    model.CurrentBid = entity.CurrentBid;
                    model.MaxBid = entity.MaxBid;
                    model.Won = entity.Won;
                    // Bid's Related Objects (Not Mapped unless Forced, or a flattening property)
                    model.LotID = entity.LotID;
                    model.LotKey = entity.Lot?.CustomKey;
                    model.LotName = entity.Lot?.Name;
                    model.UserID = entity.UserID;
                    model.UserKey = entity.User?.CustomKey;
                    // Bid's Associated Objects (Not Mapped unless Forced)
                    // Additional Mappings
                    if (CreateBidModelFromEntityHooksList != null) { model = CreateBidModelFromEntityHooksList(entity, model, contextProfileName); }
                    break;
                }
            } ///////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
            // Finished!
            return model;
        }
    }
