        public static void GenLotCategorySQLSelectorFull()
        {
            PreBuiltLotCategorySQLSelectorFull = x => x == null ? null! : new AnonLotCategory
            {
                MasterID = x.MasterID,
                Master = ModelMapperForLot.PreBuiltLotSQLSelectorList.Expand().Compile().Invoke(x.Master!),
                SlaveID = x.SlaveID,
                Slave = ModelMapperForCategory.PreBuiltCategorySQLSelectorList.Expand().Compile().Invoke(x.Slave!),
                SortOrder = x.SortOrder,
                ID = x.ID,
                CustomKey = x.CustomKey,
                CreatedDate = x.CreatedDate,
                UpdatedDate = x.UpdatedDate,
                Active = x.Active,
                Hash = x.Hash,
                JsonAttributes = x.JsonAttributes,
            };
        }

