        public static ILotCategoryModel? CreateLotCategoryModelFromEntity(
            this ILotCategory? entity,
            MappingMode mode,
            string? contextProfileName)
        {
            if (entity == null) { return null; }
            // Map the Inherited Properties
            // ReSharper disable once InvokeAsExtensionMethod
            var model = BaseModelMapper.MapBaseEntityPropertiesToModel(
                RegistryLoaderWrapper.GetInstance<ILotCategoryModel>(contextProfileName),
                Contract.RequiresNotNull(entity),
                mode,
                contextProfileName);
            // Map this level's Properties
            switch (mode)
            {
                case MappingMode.Full: //////////////////////////////////////////////////////////////////////////////////////////////////////////////
                {
                    // LotCategory's Properties
                    // LotCategory's Related Objects
                    // LotCategory's Associated Objects
                    // Additional Mappings
                    if (CreateLotCategoryModelFromEntityHooksFull != null) { model = CreateLotCategoryModelFromEntityHooksFull(entity, model, contextProfileName); }
                    goto case MappingMode.Lite;
                }
                case MappingMode.Lite: //////////////////////////////////////////////////////////////////////////////////////////////////////////////
                {
                    // LotCategory's Properties
                    // LotCategory's Related Objects (Not Mapped unless Forced, or a flattening property)
                    model.Slave = ModelMapperForCategory.CreateCategoryModelFromEntityLite(entity.Slave, contextProfileName);
                    // LotCategory's Associated Objects (Not Mapped unless Forced)
                    // Additional Mappings
                    if (CreateLotCategoryModelFromEntityHooksLite != null) { model = CreateLotCategoryModelFromEntityHooksLite(entity, model, contextProfileName); }
                    goto case MappingMode.List;
                }
                case MappingMode.List: //////////////////////////////////////////////////////////////////////////////////////////////////////////////
                default:
                {
                    // IHaveJsonAttributesBase Properties (Forced)
                    model.SerializableAttributes = entity.JsonAttributes.DeserializeAttributesDictionary();
                    // LotCategory's Properties
                    model.SortOrder = entity.SortOrder;
                    // LotCategory's Related Objects (Not Mapped unless Forced, or a flattening property)
                    model.MasterID = entity.MasterID;
                    model.MasterKey = entity.Master?.CustomKey;
                    model.MasterName = entity.Master?.Name;
                    model.SlaveID = entity.SlaveID;
                    model.SlaveKey = entity.Slave?.CustomKey;
                    model.SlaveName = entity.Slave?.Name;
                    // LotCategory's Associated Objects (Not Mapped unless Forced)
                    // Additional Mappings
                    if (CreateLotCategoryModelFromEntityHooksList != null) { model = CreateLotCategoryModelFromEntityHooksList(entity, model, contextProfileName); }
                    break;
                }
