        public static void GenStoreAuctionSQLSelectorFull()
        {
            PreBuiltStoreAuctionSQLSelectorFull = x => x == null ? null! : new AnonStoreAuction
            {
                MasterID = x.MasterID,
                Master = ModelMapperForStore.PreBuiltStoreSQLSelectorList.Expand().Compile().Invoke(x.Master!),
                SlaveID = x.SlaveID,
                Slave = ModelMapperForAuction.PreBuiltAuctionSQLSelectorList.Expand().Compile().Invoke(x.Slave!),
                IsVisibleIn = x.IsVisibleIn,
                ID = x.ID,
                CustomKey = x.CustomKey,
                CreatedDate = x.CreatedDate,
                UpdatedDate = x.UpdatedDate,
                Active = x.Active,
                Hash = x.Hash,
                JsonAttributes = x.JsonAttributes,
            };
        }

