        public static void GenFavoriteCategorySQLSelectorFull()
        {
            PreBuiltFavoriteCategorySQLSelectorFull = x => x == null ? null! : new AnonFavoriteCategory
            {
                MasterID = x.MasterID,
                Master = ModelMapperForUser.PreBuiltUserSQLSelectorList.Expand().Compile().Invoke(x.Master!),
                SlaveID = x.SlaveID,
                Slave = ModelMapperForCategory.PreBuiltCategorySQLSelectorList.Expand().Compile().Invoke(x.Slave!),
                ID = x.ID,
                CustomKey = x.CustomKey,
                CreatedDate = x.CreatedDate,
                UpdatedDate = x.UpdatedDate,
                Active = x.Active,
                Hash = x.Hash,
                JsonAttributes = x.JsonAttributes,
            };
        }

