        /// <summary>An <see cref="IUiKeyModel"/> extension method that creates a(n) <see cref="UiKey"/> entity.</summary>
        /// <param name="model">            The model to act on.</param>
        /// <param name="timestamp">        The timestamp Date/Time.</param>
        /// <param name="contextProfileName">Name of the context profile.</param>
        /// <returns>The new <see cref="UiKey"/> entity.</returns>
        public static IUiKey CreateUiKeyEntity(
            this IUiKeyModel model,
            DateTime timestamp,
            string? contextProfileName)
        {
            // Create the object and Map the Inherited Properties
            return Contract.RequiresNotNull(model)
                .CreateEntityFromModelBase<IUiKeyModel, UiKey>(timestamp, contextProfileName)
                // Use the Update method to map non-inherited properties
                .UpdateUiKeyFromModel(model, timestamp, null);
        }

        /// <summary>An <see cref="IUiKeyModel"/> extension method that updates a(n) <see cref="UiKey"/> entity.</summary>
