        /// <param name="updateTimestamp">The update timestamp Date/Time.</param>
        /// <returns>The updated <see cref="UiKey"/> entity.</returns>
        public static IUiKey UpdateUiKeyFromModel(
            this IUiKey entity,
            IUiKeyModel model,
            DateTime timestamp,
            DateTime? updateTimestamp)
        {
            // Map the Inherited Properties
            entity = Contract.RequiresNotNull(entity)
                .MapBaseModelPropertiesToEntity(Contract.RequiresNotNull(model));
            // UiKey Properties
            entity.Type = model.Type;
            // UiKey's Associated Objects
            // Finally, update the timestamp
            entity.UpdatedDate = updateTimestamp == null || updateTimestamp == DateTime.MinValue ? null : updateTimestamp;
            // Return
            return entity;
        }

        public static void GenUiKeySQLSelectorFull()
        {
            PreBuiltUiKeySQLSelectorFull = x => x == null ? null! : new AnonUiKey
            {
                Type = x.Type,
                UiTranslations = x.UiTranslations!.Where(y => y.Active).Select(ModelMapperForUiTranslation.PreBuiltUiTranslationSQLSelectorList.Expand().Compile()).ToList(),
                ID = x.ID,
                CustomKey = x.CustomKey,
                CreatedDate = x.CreatedDate,
                UpdatedDate = x.UpdatedDate,
                Active = x.Active,
                Hash = x.Hash,
                JsonAttributes = x.JsonAttributes,
            };
        }

