        public static IUiKeyModel? CreateUiKeyModelFromEntity(
            this IUiKey? entity,
            MappingMode mode,
            string? contextProfileName)
        {
            if (entity == null) { return null; }
            // Map the Inherited Properties
            // ReSharper disable once InvokeAsExtensionMethod
            var model = BaseModelMapper.MapBaseEntityPropertiesToModel(
                RegistryLoaderWrapper.GetInstance<IUiKeyModel>(contextProfileName),
                Contract.RequiresNotNull(entity),
                mode,
                contextProfileName);
            // Map this level's Properties
            switch (mode)
            {
                case MappingMode.Full: //////////////////////////////////////////////////////////////////////////////////////////////////////////////
                {
                    // UiKey's Properties
                    // UiKey's Related Objects
                    // UiKey's Associated Objects
                    model.UiTranslations = (entity is AnonUiKey ? ((AnonUiKey)entity).UiTranslations : entity.UiTranslations)?.Where(x => x.Active).Select(x => ModelMapperForUiTranslation.CreateUiTranslationModelFromEntityList(x, contextProfileName)).ToList()!;
                    // Additional Mappings
                    if (CreateUiKeyModelFromEntityHooksFull != null) { model = CreateUiKeyModelFromEntityHooksFull(entity, model, contextProfileName); }
                    goto case MappingMode.Lite;
                }
                case MappingMode.Lite: //////////////////////////////////////////////////////////////////////////////////////////////////////////////
                {
                    // UiKey's Properties
                    // UiKey's Related Objects (Not Mapped unless Forced, or a flattening property)
                    // UiKey's Associated Objects (Not Mapped unless Forced)
                    // Additional Mappings
                    if (CreateUiKeyModelFromEntityHooksLite != null) { model = CreateUiKeyModelFromEntityHooksLite(entity, model, contextProfileName); }
                    goto case MappingMode.List;
                }
                case MappingMode.List: //////////////////////////////////////////////////////////////////////////////////////////////////////////////
                default:
                {
                    // IHaveJsonAttributesBase Properties (Forced)
                    model.SerializableAttributes = entity.JsonAttributes.DeserializeAttributesDictionary();
                    // UiKey's Properties
                    model.Type = entity.Type;
                    // UiKey's Related Objects (Not Mapped unless Forced, or a flattening property)
                    // UiKey's Associated Objects (Not Mapped unless Forced)
                    // Additional Mappings
                    if (CreateUiKeyModelFromEntityHooksList != null) { model = CreateUiKeyModelFromEntityHooksList(entity, model, contextProfileName); }
                    break;
                }
            } ///////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
            // Finished!
            return model;
        }
