        public static IUiTranslation UpdateUiTranslationFromModel(
            this IUiTranslation entity,
            IUiTranslationModel model,
            DateTime timestamp,
            DateTime? updateTimestamp)
        {
            // Map the Inherited Properties
            entity = Contract.RequiresNotNull(entity)
                .MapBaseModelPropertiesToEntity(Contract.RequiresNotNull(model));
            // UiTranslation Properties
            entity.Locale = model.Locale;
            entity.Value = model.Value;
            // UiTranslation's Related Objects
            // Finally, update the timestamp
            entity.UpdatedDate = updateTimestamp == null || updateTimestamp == DateTime.MinValue ? null : updateTimestamp;
            // Return
            return entity;
        }

        public static void GenUiTranslationSQLSelectorFull()
        {
            PreBuiltUiTranslationSQLSelectorFull = x => x == null ? null! : new AnonUiTranslation
            {
                Locale = x.Locale,
                Value = x.Value,
                UiKeyID = x.UiKeyID,
                UiKey = ModelMapperForUiKey.PreBuiltUiKeySQLSelectorList.Expand().Compile().Invoke(x.UiKey!),
                ID = x.ID,
                CustomKey = x.CustomKey,
                CreatedDate = x.CreatedDate,
                UpdatedDate = x.UpdatedDate,
                Active = x.Active,
                Hash = x.Hash,
                JsonAttributes = x.JsonAttributes,
            };
        }

