        public static IUiTranslationModel? CreateUiTranslationModelFromEntity(
            this IUiTranslation? entity,
            MappingMode mode,
            string? contextProfileName)
        {
            if (entity == null) { return null; }
            // Map the Inherited Properties
            // ReSharper disable once InvokeAsExtensionMethod
            var model = BaseModelMapper.MapBaseEntityPropertiesToModel(
                RegistryLoaderWrapper.GetInstance<IUiTranslationModel>(contextProfileName),
                Contract.RequiresNotNull(entity),
                mode,
                contextProfileName);
            // Map this level's Properties
            switch (mode)
            {
                case MappingMode.Full: //////////////////////////////////////////////////////////////////////////////////////////////////////////////
                {
                    // UiTranslation's Properties
                    // UiTranslation's Related Objects
                    // UiTranslation's Associated Objects
                    // Additional Mappings
                    if (CreateUiTranslationModelFromEntityHooksFull != null) { model = CreateUiTranslationModelFromEntityHooksFull(entity, model, contextProfileName); }
                    goto case MappingMode.Lite;
                }
                case MappingMode.Lite: //////////////////////////////////////////////////////////////////////////////////////////////////////////////
                {
                    // UiTranslation's Properties
                    // UiTranslation's Related Objects (Not Mapped unless Forced, or a flattening property)
                    // UiTranslation's Associated Objects (Not Mapped unless Forced)
                    // Additional Mappings
                    if (CreateUiTranslationModelFromEntityHooksLite != null) { model = CreateUiTranslationModelFromEntityHooksLite(entity, model, contextProfileName); }
                    goto case MappingMode.List;
                }
                case MappingMode.List: //////////////////////////////////////////////////////////////////////////////////////////////////////////////
                default:
                {
                    // IHaveJsonAttributesBase Properties (Forced)
                    model.SerializableAttributes = entity.JsonAttributes.DeserializeAttributesDictionary();
                    // UiTranslation's Properties
                    model.Locale = entity.Locale;
                    model.Value = entity.Value;
                    // UiTranslation's Related Objects (Not Mapped unless Forced, or a flattening property)
                    model.UiKeyID = entity.UiKeyID;
                    model.UiKey = ModelMapperForUiKey.CreateUiKeyModelFromEntityLite(entity.UiKey, contextProfileName);
                    model.UiKeyKey = entity.UiKey?.CustomKey;
                    // UiTranslation's Associated Objects (Not Mapped unless Forced)
                    // Additional Mappings
                    if (CreateUiTranslationModelFromEntityHooksList != null) { model = CreateUiTranslationModelFromEntityHooksList(entity, model, contextProfileName); }
                    break;
