        public static void GenPaymentMethodSQLSelectorFull()
        {
            PreBuiltPaymentMethodSQLSelectorFull = x => x == null ? null! : new AnonPaymentMethod
            {
                Name = x.Name,
                Description = x.Description,
                ID = x.ID,
                CustomKey = x.CustomKey,
                CreatedDate = x.CreatedDate,
                UpdatedDate = x.UpdatedDate,
                Active = x.Active,
                Hash = x.Hash,
                JsonAttributes = x.JsonAttributes,
            };
        }

