                case MappingMode.Lite: //////////////////////////////////////////////////////////////////////////////////////////////////////////////
                {
                    // CartFile's Properties
                    model.SeoDescription = entity.SeoDescription;
                    model.SeoKeywords = entity.SeoKeywords;
                    model.SeoMetaData = entity.SeoMetaData;
                    model.SeoPageTitle = entity.SeoPageTitle;
                    // CartFile's Related Objects (Not Mapped unless Forced, or a flattening property)
                    // CartFile's Associated Objects (Not Mapped unless Forced)
                    // Additional Mappings
