        : AssociateObjectsWorkflowBase<IVisitorModel, IVisitor, IVisitModel, IVisit, Visit>
        , IVisitorWithVisitsAssociationWorkflow
    {
        /// <inheritdoc/>
        protected override ICollection<Visit>? GetObjectsCollection(IVisitor entity) { return entity.Visits; }

        /// <inheritdoc/>
        protected override List<IVisitModel>? GetModelObjectsList(IVisitorModel model) { return model.Visits; }

        /// <inheritdoc/>
        protected override async Task AddObjectToObjectsListAsync(IVisitor entity, IVisit newEntity)
        {
            if (newEntity == null) { return; }
            entity.Visits!.Add((Visit)newEntity);
        }

        /// <inheritdoc/>
        protected override void InitializeObjectListIfNull(IVisitor entity)
        {
            if (entity.Visits != null) { return; }
            entity.Visits = new HashSet<Visit>();
        }

        /// <inheritdoc/>
        protected override async Task DeactivateObjectAsync(IVisit entity, DateTime timestamp)
        {
            // Hook-in to deactivate more custom property assignments
            await DeactivateObjectAdditionalPropertiesAsync(entity, timestamp).ConfigureAwait(false);
            // Deactivate this entity
            entity.Active = false;
            entity.UpdatedDate = timestamp;
        }

        private static int UpsertThisEntityStatusAndGetIDInner(
            string? name,
            string? key,
            IStatusableBaseModel? model,
            DateTime timestamp,
            IClarityEcommerceEntities context)
        {
            var entity = context.VisitStatuses.FilterByActive(true).FilterByCustomKey(key, true).SingleOrDefault();
            if (entity == null) { entity = context.VisitStatuses.FilterByActive(true).FilterByName(name, true).SingleOrDefault(); }
            if (entity == null && !string.IsNullOrWhiteSpace(model?.CustomKey)) { entity = context.VisitStatuses.FilterByActive(true).FilterByName(model!.CustomKey, true).SingleOrDefault(); }
            if (entity == null && !string.IsNullOrWhiteSpace(model?.Name)) { entity = context.VisitStatuses.FilterByActive(true).FilterByName(model!.Name, true).SingleOrDefault(); }
            if (entity != null) { return entity.ID; } // Return existing
            // Using a separate context here so we don't save a partially modified Store
            var newEntity = RegistryLoaderWrapper.GetInstance<IVisitStatus>(context.ContextProfileName);
            newEntity.Active = true;
            newEntity.CreatedDate = timestamp;
            if (model != null)
            {
                newEntity.CustomKey = model.CustomKey;
                newEntity.Name = model.Name;
                newEntity.DisplayName = model.DisplayName;
                newEntity.SortOrder = model.SortOrder;
            }
            else
            {
                newEntity.CustomKey = key;
                newEntity.Name = name;
            }
            context.VisitStatuses.Add((VisitStatus)newEntity);
            if (!context.SaveUnitOfWork(true))
            {
                throw new InvalidOperationException("Saving the Type failed.");
            }
            return newEntity.ID;
        }

        private static int UpsertThisEntityStatusAndGetID(
            string? name,
            string? key,
            IStatusableBaseModel? model,
            DateTime timestamp,
            IClarityEcommerceEntities context)
        {
            Contract.RequiresNotNull(name, key, model);
            return UpsertThisEntityStatusAndGetIDInner(name, key, model, timestamp, context);
        }

        /// <inheritdoc/>
